private Element popStackToClose(Tag tag) {
int counter = 0;
Element elToClose = null;
for (int i = stack.size() -1; i > 0; i--) {
counter++;
Element el = stack.get(i);
Tag elTag = el.tag();
if (elTag.equals(bodyTag) || elTag.equals(htmlTag)) { // once in body, don't close past body
} else if (elTag.equals(tag)) {
elToClose = el;
break;
if (elToClose != null) {
for (int i = 0; i < counter; i++) {
stack.removeLast();
return elToClose;
}

public static TextNode createFromEncoded(String encodedText, String baseUri) {
String text = StringEscapeUtils.unescapeHtml(encodedText);
return new TextNode(text, baseUri);
}

private Tag setOptionalClosing() {
optionalClosing = true;
return this;
}

public Attribute(String key, String value) {
Validate.notEmpty(key);
Validate.notNull(value);
this.key = key.trim().toLowerCase();
this.value = value;
}

boolean canContain(Tag child) {
Validate.notNull(child);
if (child.isBlock && !this.canContainBlock)
if (!child.isBlock && !this.canContainInline) // not block == inline
if (this.optionalClosing && this.equals(child))
if (this.empty || this.isData())
if (this.tagName.equals("head")) {
if (this.tagName.equals("dt") && child.tagName.equals("dd"))
if (this.tagName.equals("dd") && child.tagName.equals("dt"))
return true;
}

void outerHtml(StringBuilder accum) {
String html = StringEscapeUtils.escapeHtml(getWholeText());
if (parent() instanceof Element && !((Element) parent()).preserveWhitespace()) {
html = normaliseWhitespace(html);
if (!isBlank() && parentNode instanceof Element && ((Element) parentNode).tag().canContainBlock()  && siblingIndex() == 0)
indent(accum);
accum.append(html);
}

private String tagName;
private boolean isBlock = true; // block or inline
private boolean canContainBlock = true; // Can this tag hold block level tags?
private boolean canContainInline = true; // only pcdata if not
private boolean optionalClosing = false; // If tag is open, and another seen, close previous tag
private boolean empty = false; // can hold nothing; e.g. img
private boolean preserveWhitespace = false; // for pre, textarea, script etc
private Tag(String tagName) {
this.tagName = tagName.toLowerCase();
}

private Element popStackToSuitableContainer(Tag tag) {
while (!stack.isEmpty()) {
if (last().tag().canContain(tag))
return last();
}

public Element first() {
return !contents.isEmpty() ? contents.get(0) : null;
}
