477: @Test public void testClonesClassnames() {
478: Document doc = Jsoup.parse("<div class='one two'></div>");
479: Element div = doc.select("div").first();
480: Set<String> classes = div.classNames();
481: assertEquals(2, classes.size());
482: assertTrue(classes.contains("one"));
483: assertTrue(classes.contains("two"));
484: 485: Element copy = div.clone();
486: Set<String> copyClasses = copy.classNames();
487: assertEquals(2, copyClasses.size());
488: assertTrue(copyClasses.contains("one"));
489: assertTrue(copyClasses.contains("two"));
490: copyClasses.add("three");
491: copyClasses.remove("one");
492: 493: assertTrue(classes.contains("one"));
494: assertFalse(classes.contains("three"));
495: assertFalse(copyClasses.contains("one"));
496: assertTrue(copyClasses.contains("three"));
497: 498: assertEquals("", div.html());
499: assertEquals("", copy.html());
500: }

