void read(Tokeniser t, CharacterReader r) {
String tagName = r.consumeTagName().toLowerCase();
t.tagPending.appendTagName(tagName);
switch (r.consume()) {
t.transition(BeforeAttributeName);
break;
t.emitTagPending();
t.transition(Data);
break;
}

abstract boolean process(Token t, HtmlTreeBuilder tb);
