public void put(Attribute attribute) {
Validate.notNull(attribute);
if (attributes == null)
attributes = new LinkedHashMap<String, Attribute>(2);
attributes.put(attribute.getKey(), attribute);
}

protected void setSiblingIndex(int siblingIndex) {
this.siblingIndex = siblingIndex;
}

public Element appendChild(Node child) {
Validate.notNull(child);
reparentChild(child);
ensureChildNodes();
childNodes.add(child);
child.setSiblingIndex(childNodes.size() - 1);
return this;
}

public Element(Tag tag, String baseUri) {
this(tag, baseUri, new Attributes());
}

public static Tag valueOf(String tagName) {
Validate.notNull(tagName);
Tag tag = tags.get(tagName);
if (tag == null) {
tagName = tagName.trim().toLowerCase();
Validate.notEmpty(tagName);
tag = tags.get(tagName);
if (tag == null) {
tag = new Tag(tagName);
tag.isBlock = false;
tag.canContainBlock = true;
return tag;
}

void outerHtmlHead(Appendable accum, int depth, Document.OutputSettings out) throws IOException {
if (out.prettyPrint() && ((siblingIndex() == 0 && parentNode instanceof Element && ((Element) parentNode).tag().formatAsBlock() && !isBlank()) || (out.outline() && siblingNodes().size()>0 && !isBlank()) ))
indent(accum, depth, out);
boolean normaliseWhite = out.prettyPrint() && parent() instanceof Element
Entities.escape(accum, getWholeText(), out, false, normaliseWhite, false);
}

public void put(String key, String value) {
Attribute attr = new Attribute(key, value);
put(attr);
}

private static CoreCharset byName(String name) {
if (name.equals("US-ASCII"))
if (name.startsWith("UTF-")) // covers UTF-8, UTF-16, et al
return utf;
return fallback;
}
