protected void outerHtml(Appendable accum) {
new NodeTraversor(new OuterHtmlVisitor(accum, getOutputSettings())).traverse(this);
}

public String getWholeDeclaration() {
final String decl = this.name;
if(decl.equals("xml") && attributes.size() > 1 ) {
StringBuilder sb = new StringBuilder(decl);
final String version = attributes.get("version");
if( version != null ) {
sb.append(" version=\"").append(version).append("\"");
final String encoding = attributes.get("encoding");
if( encoding != null ) {
sb.append(" encoding=\"").append(encoding).append("\"");
return sb.toString();
return this.name;
}

boolean rangeEquals(final int start, int count, final String cached) {
if (count == cached.length()) {
char one[] = input;
int i = start;
int j = 0;
while (count-- != 0) {
if (one[i++] != cached.charAt(j++))
return true;
}

