public Node nextSibling() {
if (parentNode == null)
return null; // root
final List<Node> siblings = parentNode.childNodes;
final int index = siblingIndex+1;
if (siblings.size() > index)
return siblings.get(index);
return null;
}

public String getWholeText() {
