public static Tag valueOf(String tagName, ParseSettings settings) {
Validate.notNull(tagName);
Tag tag = tags.get(tagName);
if (tag == null) {
tagName = settings.normalizeTag(tagName);
Validate.notEmpty(tagName);
tag = tags.get(tagName);
if (tag == null) {
tag = new Tag(tagName);
tag.isBlock = false;
tag.canContainBlock = true;
return tag;
}

void advanceTransition(TokeniserState state) {
reader.advance();
this.state = state;
}

private static final List<Node> EMPTY_NODES = Collections.emptyList();
protected Node(String baseUri, Attributes attributes) {
Validate.notNull(baseUri);
Validate.notNull(attributes);
childNodes = EMPTY_NODES;
this.baseUri = baseUri.trim();
this.attributes = attributes;
}

protected void ensureChildNodes() {
if (childNodes == EMPTY_NODES) {
childNodes = new ArrayList<Node>(4);
}

final String normalName() { // loses case, used in tree building for working out where in tree it should go
return normalName;
}

protected boolean processEndTag(String name) {
if (currentToken == end) { // don't recycle an in-use token
return process(end.reset().name(name));
}

Accumulator(Element root, Elements elements, Evaluator eval) {
this.root = root;
this.elements = elements;
this.eval = eval;
}

boolean process(Token t, HtmlTreeBuilder tb) {
if (isWhitespace(t)) {
switch (t.type) {
Token.StartTag start = t.asStartTag();
String name = start.normalName();
if (name.equals("html")) {
} else if (StringUtil.in(name, "base", "basefont", "bgsound", "command", "link")) {
} else if (name.equals("meta")) {
} else if (name.equals("title")) {
} else if (StringUtil.in(name, "noframes", "style")) {
} else if (name.equals("noscript")) {
} else if (name.equals("script")) {
} else if (name.equals("head")) {
return anythingElse(t, tb);
Token.EndTag end = t.asEndTag();
name = end.normalName();
if (name.equals("head")) {
tb.pop();
tb.transition(AfterHead);
return true;
}

final boolean isDoctype() {
