public String consumeToAny(final char... chars) {
final int start = pos;
final int remaining = length;
final char[] val = input;
OUTER: while (pos < remaining) {
for (char c : chars) {
if (val[pos] == c)
break OUTER;
pos++;
return pos > start ? cacheString(start, pos-start) : "";
}

boolean matchesLetter() {
if (isEmpty())
char c = input[pos];
return (c >= 'A' && c <= 'Z') || (c >= 'a' && c <= 'z') || Character.isLetter(c);
}

public static void notEmpty(String string) {
if (string == null || string.length() == 0)
throw new IllegalArgumentException("String must not be empty");
}

void read(Tokeniser t, CharacterReader r) {
String tagName = r.consumeTagName();
t.tagPending.appendTagName(tagName);
switch (r.consume()) {
t.transition(BeforeAttributeName);
break;
t.transition(SelfClosingStartTag);
break;
t.emitTagPending();
t.transition(Data);
break;
}

static AttributeValue valueOf(String value) {
return new AttributeValue(value);
}

public Whitelist addProtocols(String tag, String attribute, String... protocols) {
Validate.notEmpty(tag);
Validate.notEmpty(attribute);
Validate.notNull(protocols);
TagName tagName = TagName.valueOf(tag);
AttributeKey attrKey = AttributeKey.valueOf(attribute);
if (this.protocols.containsKey(tagName)) {
attrMap = new HashMap<AttributeKey, Set<Protocol>>();
this.protocols.put(tagName, attrMap);
if (attrMap.containsKey(attrKey)) {
protSet = new HashSet<Protocol>();
attrMap.put(attrKey, protSet);
for (String protocol : protocols) {
Validate.notEmpty(protocol);
Protocol prot = Protocol.valueOf(protocol);
protSet.add(prot);
return this;
}
