910: for (int i = 0; i < 25000; i++) {
911: longBody.append(i).append("<dl><dd>");
912: }
913: for (int i = 0; i < 25000; i++) {
914: longBody.append(i).append("</dd></dl>");
915: }
916: 917: // Act
918: long start = System.currentTimeMillis();
919: Document doc = Parser.parseBodyFragment(longBody.toString(), "");
920: 921: // Assert
922: assertEquals(2, doc.body().childNodeSize());
923: assertEquals(25000, doc.select("dd").size());
924: assertTrue(System.currentTimeMillis() - start < 1000);
925: }

