public String attr(String key) {
Validate.notNull(key);
if (!hasAttributes()) {
return key.equals(nodeName()) ? (String) value : EmptyString;
}

private void checkCapacity(int minNewSize) {
Validate.isTrue(minNewSize >= size);
int curSize = keys.length;
if (curSize >= minNewSize)
return;
int newSize = curSize >= InitialCapacity ? size * GrowthFactor : InitialCapacity;
if (minNewSize > newSize)
keys = copyOf(keys, newSize);
vals = copyOf(vals, newSize);
}

private static boolean isWhitespace(String data) {
for (int i = 0; i < data.length(); i++) {
char c = data.charAt(i);
if (!StringUtil.isWhitespace(c))
return true;
}

private void add(String key, String value) {
checkCapacity(size + 1);
keys[size] = key;
vals[size] = value;
size++;
}

public Document parseInput(Reader inputHtml, String baseUri) {
errors = isTrackErrors() ? ParseErrorList.tracking(maxErrors) : ParseErrorList.noTracking();
return treeBuilder.parse(inputHtml, baseUri, errors, settings);
}

static Document parseInputStream(InputStream input, String charsetName, String baseUri, Parser parser) throws IOException  {
if (input == null) // empty body
input = ConstrainableInputStream.wrap(input, bufferSize, 0);
Document doc = null;
boolean fullyRead = false;
input.mark(firstReadBufferSize);
ByteBuffer firstBytes = readToByteBuffer(input, firstReadBufferSize - 1); // -1 because we read one more to see if completed
fullyRead = input.read() == -1;
input.reset();
BomCharset bomCharset = detectCharsetFromBom(firstBytes);
if (bomCharset != null) {
if (charsetName == null) { // determine from meta. safe first parse as UTF-8
Validate.notEmpty(charsetName, "Must set charset arg to character set of file to parse. Set to null to attempt to detect from HTML");
if (doc == null) {
if (charsetName == null)
BufferedReader reader = new BufferedReader(new InputStreamReader(input, charsetName), bufferSize);
doc = parser.parseInput(reader, baseUri);
doc.outputSettings().charset(charsetName);
input.close();
return doc;
}

public Attribute(String key, String val, Attributes parent) {
Validate.notNull(key);
this.key = key.trim();
Validate.notEmpty(key); // trimming could potentially make empty, so validate here
this.val = val;
this.parent = parent;
}
