106: assertEquals("path", path.getLocalName());
107: assertEquals("path", path.getNodeName());
108: 109: Node clip = path.getChildNodes().item(1);
110: assertEquals("http://example.com/clip", clip.getNamespaceURI());
111: assertEquals("clip", clip.getLocalName());
112: assertEquals("clip", clip.getNodeName());
113: assertEquals("456", clip.getTextContent());
114: 115: Node picture = svg.getNextSibling().getNextSibling();
116: assertEquals("http://www.w3.org/1999/xhtml", picture.getNamespaceURI());
117: assertEquals("picture", picture.getLocalName());
118: assertEquals("picture", picture.getNodeName());
119: 120: Node img = picture.getFirstChild();
121: assertEquals("http://www.w3.org/1999/xhtml", img.getNamespaceURI());
122: assertEquals("img", img.getLocalName());
123: assertEquals("img", img.getNodeName());
