static String getCharsetFromContentType(String contentType) {
if (contentType == null) return null;
Matcher m = charsetPattern.matcher(contentType);
if (m.find()) {
String charset = m.group(1).trim();
charset = charset.replace("charset=", "");
return validateCharset(charset);
}

public ConstrainableInputStream timeout(long startTimeNanos, long timeoutMillis) {
this.startTime = startTimeNanos;
this.timeout = timeoutMillis * 1000000;
return this;
}

public String consumeToAny(final char... chars) {
bufferUp();
final int start = bufPos;
final int remaining = bufLength;
final char[] val = charBuf;
OUTER: while (bufPos < remaining) {
for (char c : chars) {
if (val[bufPos] == c)
break OUTER;
bufPos++;
return bufPos > start ? cacheString(charBuf, stringCache, start, bufPos -start) : "";
}

protected boolean process(Token token) {
currentToken = token;
