int curSize = keys.length;
if (curSize >= minNewSize)
return;
int newSize = curSize >= InitialCapacity ? size * GrowthFactor : InitialCapacity;
if (minNewSize > newSize)
keys = copyOf(keys, newSize);
vals = copyOf(vals, newSize);
}
