int i = 0;
final String key = conn.getHeaderFieldKey(i);
final String val = conn.getHeaderField(i);
if (key == null && val == null)
break;
i++;
if (key == null || val == null)
if (headers.containsKey(key))
final ArrayList<String> vals = new ArrayList<>();
vals.add(val);
headers.put(key, vals);
}
