if (localCapped) { // this local byteBuffer cap may be smaller than the overall maxSize (like when reading first bytes)
if (read >= remaining) {
remaining -= read;
outStream.write(readBuffer, 0, read);
return ByteBuffer.wrap(outStream.toByteArray());
