void read(Tokeniser t, CharacterReader r) {
if (r.matchesLetter()) {
String name = r.consumeLetterSequence();
t.tagPending.appendTagName(name);
t.dataBuffer.append(name);
return;
char c = r.consume();
switch (c) {
if (t.isAppropriateEndTagToken()) {
t.emitTagPending();
t.transition(Data);
}

public Parser(TreeBuilder treeBuilder) {
