errors = ParseErrorList.noTracking();
}

void popStackToClose(String elName) {
for (int pos = stack.size() -1; pos >= 0; pos--) {
Element next = stack.get(pos);
stack.remove(pos);
if (next.normalName().equals(elName))
break;
}

