protected EmbeddedRungeKuttaIntegrator(final String name, final boolean fsal,
super(name, minStep, maxStep, scalAbsoluteTolerance, scalRelativeTolerance);
this.fsal      = fsal;
this.c         = c;
this.a         = a;
this.b         = b;
