protected BaseSecantSolver(final double absoluteAccuracy, final Method method) {
super(absoluteAccuracy);
this.allowed = AllowedSolution.ANY_SIDE;
this.method = method;
}

protected void setup(int maxEval,
MathUtils.checkNotNull(f);
searchMin = min;
searchMax = max;
searchStart = startValue;
function = f;
evaluations.setMaximalCount(maxEval);
evaluations.resetCount();
}

protected void verifyBracketing(final double lower,
UnivariateRealSolverUtils.verifyBracketing(function, lower, upper);
}

public double solve(final int maxEval, final UnivariateRealFunction f,
return solve(maxEval, f, min, max, startValue, AllowedSolution.ANY_SIDE);
}

public static boolean isBracketing(UnivariateRealFunction function,
if (function == null) {
final double fLo = function.value(lower);
final double fHi = function.value(upper);
return (fLo >= 0 && fHi <= 0) || (fLo <= 0 && fHi >= 0);
}
