public static Dfp computeExp(final Dfp a, final Dfp one) {
Dfp y  = new Dfp(one);
Dfp py = new Dfp(one);
Dfp f  = new Dfp(one);
Dfp fi = new Dfp(one);
Dfp x  = new Dfp(one);
for (int i = 0; i < 10000; i++) {
