private void qTy(double[] y) {
for (int k = 0; k < cols; ++k) {
int pk = permutation[k];
double gamma = 0;
for (int i = k; i < rows; ++i) {
gamma += weightedResidualJacobian[i][pk] * y[i];
gamma *= beta[pk];
for (int i = k; i < rows; ++i) {
y[i] -= gamma * weightedResidualJacobian[i][pk];
}

