if (inc) {
int rh = 1;
for (int i = 0; i < mant.length; i++) {
final int r = mant[i] + rh;
rh = r / RADIX;
mant[i] = r - rh * RADIX;
if (rh != 0) {
if (exp < MIN_EXP) {
if (exp > MAX_EXP) {
if (n != 0) {
field.setIEEEFlagsBits(DfpField.FLAG_INEXACT);
return DfpField.FLAG_INEXACT;
return 0;
}

private static double log(final double x, final double[] hiPrec) {
if (x==0) { // Handle special case of +0/-0
long bits = Double.doubleToLongBits(x);
if ((bits & 0x8000000000000000L) != 0 || x != x) {
if (x == Double.POSITIVE_INFINITY) {
int exp = (int)(bits >> 52)-1023;
if ((bits & 0x7ff0000000000000L) == 0) {
if (exp == -1 || exp == 0) {
if (x < 1.01 && x > 0.99 && hiPrec == null) {
final double[] lnm = lnMant.LN_MANT[(int)((bits & 0x000ffc0000000000L) >> 42)];
final double epsilon = (bits & 0x3ffffffffffL) / (TWO_POWER_52 + (bits & 0x000ffc0000000000L));
double lnza = 0.0;
double lnzb = 0.0;
if (hiPrec != null) {
double tmp = epsilon * HEX_40000000;
double aa = epsilon + tmp - tmp;
double ab = epsilon - aa;
double xa = aa;
double xb = ab;
final double numer = bits & 0x3ffffffffffL;
final double denom = TWO_POWER_52 + (bits & 0x000ffc0000000000L);
aa = numer - xa*denom - xb * denom;
xb += aa / denom;
final double[] lnCoef_last = LN_HI_PREC_COEF[LN_HI_PREC_COEF.length-1];
double ya = lnCoef_last[0];
double yb = lnCoef_last[1];
for (int i = LN_HI_PREC_COEF.length - 2; i >= 0; i--) {
aa = ya * xa;
ab = ya * xb + yb * xa + yb * xb;
tmp = aa * HEX_40000000;
ya = aa + tmp - tmp;
yb = aa - ya + ab;
final double[] lnCoef_i = LN_HI_PREC_COEF[i];
aa = ya + lnCoef_i[0];
ab = yb + lnCoef_i[1];
tmp = aa * HEX_40000000;
ya = aa + tmp - tmp;
yb = aa - ya + ab;
aa = ya * xa;
ab = ya * xb + yb * xa + yb * xb;
lnza = aa + ab;
lnzb = -(lnza - aa - ab);
} else {
double a = LN_2_A*exp;
double b = 0.0;
double c = a+lnm[0];
double d = -(c-a-lnm[0]);
a = c;
b = b + d;
c = a + lnza;
d = -(c - a - lnza);
a = c;
b = b + d;
c = a + LN_2_B*exp;
d = -(c - a - LN_2_B*exp);
a = c;
b = b + d;
c = a + lnm[1];
d = -(c - a - lnm[1]);
a = c;
b = b + d;
c = a + lnzb;
d = -(c - a - lnzb);
a = c;
b = b + d;
if (hiPrec != null) {
hiPrec[0] = a;
hiPrec[1] = b;
return a + b;
}

public void setSeed(int seed) {
long longMT = seed;
mt[0]= (int) longMT;
for (mti = 1; mti < N; ++mti) {
longMT = (1812433253l * (longMT ^ (longMT >> 30)) + mti) & 0xffffffffL;
mt[mti]= (int) longMT;
clear(); // Clear normal deviate cache
}

public void setSeed(long seed) {
setSeed(new int[] { (int) (seed >>> 32), (int) (seed & 0xffffffffl) });
}

private Dfp[] split(final String a) {
Dfp result[] = new Dfp[2];
boolean leading = true;
int sp = 0;
int sig = 0;
char[] buf = new char[a.length()];
for (int i = 0; i < buf.length; i++) {
buf[i] = a.charAt(i);
if (buf[i] >= '1' && buf[i] <= '9') {
leading = false;
if (buf[i] == '.') {
sig += (400 - sig) % 4;
leading = false;
if (sig == (radixDigits / 2) * 4) {
sp = i;
break;
if (buf[i] >= '0' && buf[i] <= '9' && !leading) {
sig ++;
result[0] = new Dfp(this, new String(buf, 0, sp));
for (int i = 0; i < buf.length; i++) {
buf[i] = a.charAt(i);
if (buf[i] >= '0' && buf[i] <= '9' && i < sp) {
buf[i] = '0';
result[1] = new Dfp(this, new String(buf));
return result;
}
