private boolean rebalanceLeftShrunk() {
switch (skew) {
skew = Skew.BALANCED;
return true;
if (right.skew == Skew.RIGHT_HIGH) {
rotateCCW();
skew      = Skew.BALANCED;
left.skew = Skew.BALANCED;
return true;
} else if (right.skew == Skew.BALANCED) {
final Skew s = right.left.skew;
right.rotateCW();
rotateCCW();
switch (s) {
left.skew  = Skew.BALANCED;
right.skew = Skew.BALANCED;
skew = Skew.BALANCED;
return true;
skew = Skew.RIGHT_HIGH;
return false;
}

public void visit(final BSPTreeVisitor<S> visitor) {
if (cut == null) {
visitor.visitLeafNode(this);
switch (visitor.visitOrder(this)) {
plus.visit(visitor);
visitor.visitInternalNode(this);
minus.visit(visitor);
break;
minus.visit(visitor);
visitor.visitInternalNode(this);
plus.visit(visitor);
break;
}

