public Dfp(final Dfp d) {
mant  = d.mant.clone();
sign  = d.sign;
exp   = d.exp;
nans  = d.nans;
field = d.field;
}

public static Dfp pow(Dfp base, int a)
boolean invert = false;
Dfp result = base.getOne();
if (a == 0) {
if (a < 0) {
invert = true;
a = -a;
Dfp r = new Dfp(base);
int trial = 1;
prevr = new Dfp(r);
prevtrial = trial;
r = r.multiply(r);
trial = trial * 2;
} while (a>trial);
r = prevr;
trial = prevtrial;
a = a - trial;
result = result.multiply(r);
} while (a >= 1);
if (invert) {
result = base.getOne().divide(result);
return base.newInstance(result);
}
