xNormSqr += c * c;
final double a = (hK[k + 1] > 0) ? -FastMath.sqrt(xNormSqr) : FastMath.sqrt(xNormSqr);
secondary[k] = a;
if (a != 0.0) {
hK[k + 1] -= a;
final double beta = -1 / (a * hK[k + 1]);
Arrays.fill(z, k + 1, m, 0);
for (int i = k + 1; i < m; ++i) {
final double[] hI = householderVectors[i];
final double hKI = hK[i];
double zI = hI[i] * hKI;
for (int j = i + 1; j < m; ++j) {
final double hIJ = hI[j];
zI   += hIJ * hK[j];
z[j] += hIJ * hKI;
z[i] = beta * (z[i] + zI);
double gamma = 0;
for (int i = k + 1; i < m; ++i) {
gamma += z[i] * hK[i];
gamma *= beta / 2;
for (int i = k + 1; i < m; ++i) {
