protected void parseOptimizationData(OptimizationData... optData) {
super.parseOptimizationData(optData);
for (OptimizationData data : optData) {
if (data instanceof InitialGuess) {
start = ((InitialGuess) data).getInitialGuess();
continue;
if (data instanceof SimpleBounds) {
final SimpleBounds bounds = (SimpleBounds) data;
lowerBound = bounds.getLower();
upperBound = bounds.getUpper();
continue;
checkParameters();
}

public double nextGaussian() {
if (Double.isNaN(nextGaussian)) {
final double x = nextDouble();
final double y = nextDouble();
final double alpha = 2 * FastMath.PI * x;
final double r      = FastMath.sqrt(-2 * FastMath.log(y));
random       = r * FastMath.cos(alpha);
nextGaussian = r * FastMath.sin(alpha);
} else {
random = nextGaussian;
nextGaussian = Double.NaN;
return random;
}

public RealMatrix scalarAdd(final double d) {
final int rowCount    = getRowDimension();
final int columnCount = getColumnDimension();
final RealMatrix out = createMatrix(rowCount, columnCount);
for (int row = 0; row < rowCount; ++row) {
for (int col = 0; col < columnCount; ++col) {
out.setEntry(row, col, getEntry(row, col) + d);
return out;
}

private double[][] copyOut() {
final int nRows = this.getRowDimension();
final double[][] out = new double[nRows][this.getColumnDimension()];
for (int i = 0; i < nRows; i++) {
System.arraycopy(data[i], 0, out[i], 0, data[i].length);
return out;
}

private void transform() {
final int m = householderVectors.length;
final double[] z = new double[m];
for (int k = 0; k < m - 1; k++) {
final double[] hK = householderVectors[k];
main[k] = hK[k];
double xNormSqr = 0;
for (int j = k + 1; j < m; ++j) {
final double c = hK[j];
xNormSqr += c * c;
final double a = (hK[k + 1] > 0) ? -FastMath.sqrt(xNormSqr) : FastMath.sqrt(xNormSqr);
secondary[k] = a;
if (a != 0.0) {
hK[k + 1] -= a;
final double beta = -1 / (a * hK[k + 1]);
Arrays.fill(z, k + 1, m, 0);
for (int i = k + 1; i < m; ++i) {
final double[] hI = householderVectors[i];
final double hKI = hK[i];
double zI = hI[i] * hKI;
for (int j = i + 1; j < m; ++j) {
final double hIJ = hI[j];
zI   += hIJ * hK[j];
z[j] += hIJ * hKI;
z[i] = beta * (z[i] + zI);
double gamma = 0;
for (int i = k + 1; i < m; ++i) {
gamma += z[i] * hK[i];
gamma *= beta / 2;
for (int i = k + 1; i < m; ++i) {
z[i] -= gamma * hK[i];
for (int i = k + 1; i < m; ++i) {
final double[] hI = householderVectors[i];
for (int j = i; j < m; ++j) {
hI[j] -= hK[i] * z[j] + z[i] * hK[j];
main[m - 1] = householderVectors[m - 1][m - 1];
}

private static int[] reverse(final int[] indices) {
final int[] reverse = new int[indices.length];
for (int i = 0; i < indices.length; i++) {
reverse[i] = indices[indices.length - i - 1];
return reverse;
}

public static int min(final int a, final int b) {
return (a <= b) ? a : b;
}

public static boolean equals(double x, double y, double eps) {
return equals(x, y, 1) || FastMath.abs(y - x) <= eps;
}
