private double penalty(final double[] x, final double[] repaired) {
double penalty = 0;
for (int i = 0; i < x.length; i++) {
double diff = Math.abs(x[i] - repaired[i]);
penalty += diff * valueRange;
return isMinimize ? penalty : -penalty;
}
