protected void discardArtificialVariables() {
if (numArtificialVariables == 0) {
int width = getWidth() - numArtificialVariables - 1;
int height = getHeight() - 1;
double[][] matrix = new double[height][width];
for (int i = 0; i < height; i++) {
for (int j = 0; j < width - 1; j++) {
matrix[i][j] = getEntry(i + 1, j + 1);
matrix[i][width - 1] = getEntry(i + 1, getRhsOffset());
this.tableau = new RealMatrixImpl(matrix);
this.numArtificialVariables = 0;
}

private int getConstraintTypeCounts(final Relationship relationship) {
int count = 0;
for (final LinearConstraint constraint : constraints) {
if (constraint.getRelationship() == relationship) {
++count;
return count;
}

public RealPointValuePair optimize(final LinearObjectiveFunction f,
this.f                     = f;
this.constraints           = constraints;
this.goalType              = goalType;
this.restrictToNonNegative = restrictToNonNegative;
iterations = 0;
return doOptimize();
}

public LinearObjectiveFunction(double[] coefficients, double constantTerm) {
this(new RealVectorImpl(coefficients), constantTerm);
}

public static int compareTo(double x, double y, double eps) {
if (equals(x, y, eps)) {
return 0;
} else if (x < y) {
return -1;
return 1;
}

public static boolean equals(double x, double y) {
return ((Double.isNaN(x) && Double.isNaN(y)) || x == y);
}

public void setMaxIterations(int maxIterations) {
this.maxIterations = maxIterations;
}

private Integer getPivotRow(final int col, final SimplexTableau tableau) {
double minRatio = Double.MAX_VALUE;
Integer minRatioPos = null;
for (int i = tableau.getNumObjectiveFunctions(); i < tableau.getHeight(); i++) {
double rhs = tableau.getEntry(i, tableau.getWidth() - 1);
if (MathUtils.compareTo(tableau.getEntry(i, col), 0, epsilon) >= 0) {
double ratio = rhs / tableau.getEntry(i, col);
if (ratio < minRatio) {
minRatio = ratio;
minRatioPos = i;
return minRatioPos;
}

private Integer getPivotColumn(SimplexTableau tableau) {
double minValue = 0;
Integer minPos = null;
for (int i = tableau.getNumObjectiveFunctions(); i < tableau.getWidth() - 1; i++) {
if (MathUtils.compareTo(tableau.getEntry(0, i), minValue, epsilon) < 0) {
minValue = tableau.getEntry(0, i);
minPos = i;
return minPos;
}
