public void enableAccess(Field field) {
wasAccessible = field.isAccessible();
field.setAccessible(true);
}

public InvocationMatcher(Invocation invocation, List<Matcher> matchers) {
this.invocation = invocation;
if (matchers.isEmpty()) {
this.matchers = invocation.argumentsToMatchers();
}

private Class<?> createProxyClass(Class<?> mockedType, Class<?>...interfaces) {
if (mockedType == Object.class) {
Enhancer enhancer = new Enhancer() {
enhancer.setClassLoader(SearchingClassLoader.combineLoadersOf(mockedType));
enhancer.setUseFactory(true);
if (mockedType.isInterface()) {
enhancer.setSuperclass(Object.class);
enhancer.setInterfaces(prepend(mockedType, interfaces));
enhancer.setCallbackTypes(new Class[]{MethodInterceptor.class, NoOp.class});
enhancer.setCallbackFilter(IGNORE_BRIDGE_METHODS);
if (mockedType.getSigners() != null) {
enhancer.setNamingPolicy(MockitoNamingPolicy.INSTANCE);
return enhancer.createClass();
}

public static MockSettings withSettings() {
return new MockSettingsImpl().defaultAnswer(RETURNS_DEFAULTS);
}

public void verificationStarted(VerificationMode verify) {
validateState();
resetOngoingStubbing();
verificationMode = new Localized(verify);
}

private static ClassLoader combine(List<ClassLoader> parentLoaders) {
ClassLoader loader = parentLoaders.get(parentLoaders.size()-1);
for (int i = parentLoaders.size()-2; i >= 0; i--) {
return loader;
}

public void mockingStarted(Object mock, Class classToMock, MockSettings mockSettings) {
if (listener != null && listener instanceof MockingStartedListener) {
validateState();
}

protected String toString(List<Matcher> matchers, PrintSettings printSettings) {
MatchersPrinter matchersPrinter = new MatchersPrinter();
String method = qualifiedMethodName();
String invocation = method + matchersPrinter.getArgumentsLine(matchers, printSettings);
if (printSettings.isMultiline() || (!matchers.isEmpty() && invocation.length() > MAX_LINE_LENGTH)) {
return invocation;
}
