public OngoingInjecter filterCandidate(final Collection<Object> mocks, final Field field, final Object fieldInstance) {
if(mocks.size() == 1) {
final Object matchingMock = mocks.iterator().next();
return new OngoingInjecter() {
new FieldSetter(fieldInstance, field).set(matchingMock);
}
return true;
return new OngoingInjecter() {
return false;
}

public void run(final RunNotifier notifier) {
runner.run(notifier);
}

private Object processAnnotationOn(Mock annotation, Field field) {
MockSettings mockSettings = Mockito.withSettings();
if (annotation.extraInterfaces().length > 0) { // never null
if ("".equals(annotation.name())) {
mockSettings.name(field.getName());
mockSettings.defaultAnswer(annotation.answer().get());
return Mockito.mock(field.getType(), mockSettings);
}

private Class<?> createProxyClass(Class<?> mockedType, Class<?>...interfaces) {
if (mockedType == Object.class) {
Enhancer enhancer = new Enhancer() {
enhancer.setClassLoader(SearchingClassLoader.combineLoadersOf(mockedType));
enhancer.setUseFactory(true);
if (mockedType.isInterface()) {
enhancer.setSuperclass(Object.class);
enhancer.setInterfaces(prepend(mockedType, interfaces));
enhancer.setCallbackTypes(new Class[]{MethodInterceptor.class, NoOp.class});
enhancer.setCallbackFilter(IGNORE_BRIDGE_METHODS);
if (mockedType.getSigners() != null) {
enhancer.setNamingPolicy(MockitoNamingPolicy.INSTANCE);
return enhancer.createClass();
}

private Object acquireFieldInstance(Object testClass, Field field) throws IllegalAccessException {
Object fieldInstance = field.get(testClass);
if(fieldInstance != null) {
initializeField(testClass, field);
return field.get(testClass);
}

public Object createMockFor(Annotation annotation, Field field) {
return delegate.createMockFor(annotation, field);
}

public boolean isNull() {
return field.get(target) == null;
}
