private Class<?>[] prepend(Class<?> first, Class<?>... rest) {
Class<?>[] all = new Class<?>[rest.length+1];
all[0] = first;
System.arraycopy(rest, 0, all, 1, rest.length);
return all;
}

public static ClassLoader combineLoadersOf(Class<?>... classes) {
return combineLoadersOf(classes[0], classes);
}

public MockName(String mockName, Class classToMock) {
if (mockName == null) {
this.mockName = toInstanceName(classToMock);
this.surrogate = true;
this.mockName = mockName;
}

private Object createProxy(Class<?> proxyClass, final MethodInterceptor interceptor) {
Factory proxy = (Factory) objenesis.newInstance(proxyClass);
proxy.setCallbacks(new Callback[] {interceptor, SerializableNoOp.SERIALIZABLE_INSTANCE });
return proxy;
}

public static boolean isToString(InvocationOnMock invocation) {
return new ObjectMethodsGuru().isToString(invocation.getMethod());
}

public InvocationMatcher(Invocation invocation, List<Matcher> matchers) {
this.invocation = invocation;
if (matchers.isEmpty()) {
this.matchers = invocation.argumentsToMatchers();
this.matchers = matchers;
}

private Class<?> createProxyClass(Class<?> mockedType, Class<?>...interfaces) {
if (mockedType == Object.class) {
Enhancer enhancer = new Enhancer() {
}
enhancer.setClassLoader(SearchingClassLoader.combineLoadersOf(mockedType));
enhancer.setUseFactory(true);
if (mockedType.isInterface()) {
enhancer.setSuperclass(Object.class);
enhancer.setInterfaces(prepend(mockedType, interfaces));
enhancer.setCallbackTypes(new Class[]{MethodInterceptor.class, NoOp.class});
enhancer.setCallbackFilter(IGNORE_BRIDGE_METHODS);
if (mockedType.getSigners() != null) {
enhancer.setNamingPolicy(MockitoNamingPolicy.INSTANCE);
return enhancer.createClass();
}

