public OngoingStubbingImpl(MockitoStubber mockitoStubber,
RegisteredInvocations registeredInvocations) {
this.mockitoStubber = mockitoStubber;
this.registeredInvocations = registeredInvocations;
}

private static String toInstanceName(Class<?> clazz) {
String className = clazz.getSimpleName();
return className.substring(0, 1).toLowerCase() + className.substring(1);
}

public OngoingStubbing<T> thenAnswer(Answer<?> answer) {
registeredInvocations.removeLast();
mockitoStubber.addAnswer(answer);
return new ConsecutiveStubbing<T>(mockitoStubber);
}

public InvocationMatcher bindMatchers(ArgumentMatcherStorage argumentMatcherStorage, Invocation invocation) {
List<Matcher> lastMatchers = argumentMatcherStorage.pullMatchers();
validateMatchers(invocation, lastMatchers);
InvocationMatcher invocationWithMatchers = new InvocationMatcher(invocation, lastMatchers);
return invocationWithMatchers;
}

private static void addIfNewElement(List<ClassLoader> loaders, ClassLoader c) {
if (c != null && !loaders.contains(c)) {
loaders.add(c);
}
