public static Set<Object> newMockSafeHashSet(Object... mocks) {
return HashCodeAndEqualsSafeSet.of(mocks);
}

public OngoingMockInjection tryConstructorInjection() {
injectionStrategies.thenTry(new ConstructorInjection());
return this;
