private Object preparedMock(Object instance, Field field) {
if (isAnnotatedByMockOrSpy(field)) {
return instance;
} else if (isMockOrSpy(instance)) {
mockUtil.maybeRedefineMockName(instance, field.getName());
return instance;
return null;
}

