static void scan(Object testClass, Class<?> clazz) {
AnnotationEngine annotationEngine = new GlobalConfiguration().getAnnotationEngine();
Field[] fields = clazz.getDeclaredFields();
for (Field field : fields) {
if (annotationEngine.getClass() != new DefaultMockitoConfiguration().getAnnotationEngine().getClass()) {
processAnnotationDeprecatedWay(annotationEngine, testClass, field);
annotationEngine.process(clazz, testClass);
}

public static MockSettings withSettings() {
return new MockSettingsImpl().defaultAnswer(RETURNS_DEFAULTS);
}

public static void initMocks(Object testClass) {
if (testClass == null) {
Class<?> clazz = testClass.getClass();
while (clazz != Object.class) {
scan(testClass, clazz);
clazz = clazz.getSuperclass();
}

public Object answer(InvocationOnMock invocation) throws Throwable {
return new GlobalConfiguration().getDefaultAnswer().answer(invocation);
}

public boolean isOut(Invocation invocation) {
return Invocation.isToString(invocation);
}

public boolean isToString(MockitoMethod method) {
return method.getReturnType() == String.class && method.getParameterTypes().length == 0
}

protected String toString(List<Matcher> matchers, PrintSettings printSettings) {
MatchersPrinter matchersPrinter = new MatchersPrinter();
String method = qualifiedMethodName();
String invocation = method + matchersPrinter.getArgumentsLine(matchers, printSettings);
if (printSettings.isMultiline() || (!matchers.isEmpty() && invocation.length() > MAX_LINE_LENGTH)) {
return invocation;
}

private <T> MethodInterceptorFilter getInterceptor(T mock) {
if (!(mock instanceof Factory)) {
Factory factory = (Factory) mock;
Callback callback = factory.getCallback(0);
if (callback instanceof MethodInterceptorFilter) {
return (MethodInterceptorFilter) callback;
}
