public InvocationMatcher bindMatchers(ArgumentMatcherStorage argumentMatcherStorage, Invocation invocation) {
List<Matcher> lastMatchers = argumentMatcherStorage.pullMatchers();
validateMatchers(invocation, lastMatchers);
InvocationMatcher invocationWithMatchers = new InvocationMatcher(invocation, lastMatchers);
return invocationWithMatchers;
}

public Object answer(InvocationOnMock invocation) throws Throwable {
return new GlobalConfiguration().getDefaultAnswer().answer(invocation);
}

public void verificationStarted(VerificationMode verify) {
validateState();
resetOngoingStubbing();
verificationMode = new Localized(verify);
}

public static Times times(int wantedNumberOfInvocations) {
return new Times(wantedNumberOfInvocations);
}

public <T> T verify(T mock, VerificationMode mode) {
if (mock == null) {
} else if (!mockUtil.isMock(mock)) {
mockingProgress.verificationStarted(mode);
return mock;
}

private static ClassLoader combine(List<ClassLoader> parentLoaders) {
ClassLoader loader = parentLoaders.get(parentLoaders.size()-1);
for (int i = parentLoaders.size()-2; i >= 0; i--) {
return loader;
}

public DelegatingMethod(Method method) {
assert method != null : "Method cannot be null";
this.method = method;
}

public static synchronized int next() {
return sequenceNumber++;
}
