public void test_DateTime_constructor_Moscow_Autumn() {
DateTime dt = new DateTime(2007, 10, 28, 2, 30, ZONE_MOSCOW);
assertEquals("2007-10-28T02:30:00.000+04:00", dt.toString());
}

