LocalDate date = new LocalDate(2003, 6, 30, GJ_CHRONOLOGY);
LocalDate expected = new LocalDate(-2, 6, 30, GJ_CHRONOLOGY);
assertEquals(expected, date.plusYears(-2004));
}

