checkPermission();
cMillisProvider = new FixedMillisProvider(fixedMillis);
}

public int countFieldsToPrint(ReadablePeriod period, int stopAt, Locale locale) {
if (stopAt <= 0) {
if (iPrintZeroSetting == PRINT_ZERO_ALWAYS || getFieldValue(period) != Long.MAX_VALUE) {
return 1;
return 0;
}

public static PeriodFormatter standard() {
if (cStandard == null) {
return cStandard;
}

boolean setIndexedField(ReadablePeriod period, int index, int[] values, int newValue) {
int realIndex = iIndices[index];
if (realIndex == -1) {
values[realIndex] = newValue;
return true;
}

public int[] getValues() {
int[] result = new int[size()];
for (int i = 0; i < result.length; i++) {
result[i] = getValue(i);
return result;
}

public static void setDefault(DateTimeZone zone) throws SecurityException {
SecurityManager sm = System.getSecurityManager();
if (sm != null) {
if (zone == null) {
synchronized(DateTimeZone.class) {
cDefault = zone;
}
}

protected BasePeriod(int[] values, PeriodType type) {
super();
iType = type;
iValues = values;
}
