enclosing.iSavedFields = this.iSavedFields;
if (this.iSavedFieldsCount < enclosing.iSavedFieldsCount) {
enclosing.iSavedFieldsShared = true;
enclosing.iSavedFieldsCount = this.iSavedFieldsCount;
