public final DateTimeField secondOfMinute() {
return iSecondOfMinute;
}

public long add(long instant, int value) {
return FieldUtils.safeAdd(instant, value);
}

int getDayOfYear(long instant, int year) {
long yearStart = getYearMillis(year);
return (int) ((instant - yearStart) / DateTimeConstants.MILLIS_PER_DAY) + 1;
}

public DateTimeZone getZone() {
if ((base = iBase) != null) {
return base.getZone();
}

public BaseDateTime(long instant, Chronology chronology) {
super();
iChronology = checkChronology(chronology);
iMillis = checkInstant(instant, iChronology);
}

private long setDayOfWeek(Chronology chrono, long instant) {
int dayOfWeek = chrono.dayOfWeek().get(instant);
int daysToAdd = iDayOfWeek - dayOfWeek;
if (daysToAdd != 0) {
if (iAdvance) {
if (daysToAdd < 0) {
if (daysToAdd > 0) {
daysToAdd -= 7;
instant = chrono.dayOfWeek().add(instant, daysToAdd);
return instant;
}

public final boolean isPrecise() {
return true;
}

int getMillisOfDay(long instant) {
if (instant >= 0) {
return (int) (instant % DateTimeConstants.MILLIS_PER_DAY);
}

private int digitCount(String text, int position, int amount) {
int limit = Math.min(text.length() - position, amount);
amount = 0;
for (; limit > 0; limit--) {
char c = text.charAt(position + amount);
if (c < '0' || c > '9') {
amount++;
return amount;
}

int getDayOfMonth(long millis, int year) {
int month = getMonthOfYear(millis, year);
return getDayOfMonth(millis, year, month);
}
