public static DurationFieldType months() {
return MONTHS_TYPE;
}

protected BasePeriod(int years, int months, int weeks, int days,
super();
type = checkPeriodType(type);
iType = type;
iValues = setPeriodInternal(years, months, weeks, days, hours, minutes, seconds, millis); // internal method
}

public Period withYears(int years) {
int[] values = getValues();  // cloned
getPeriodType().setIndexedField(this, PeriodType.YEAR_INDEX, values, years);
return new Period(values, getPeriodType());
}

public static void setDefault(DateTimeZone zone) throws SecurityException {
SecurityManager sm = System.getSecurityManager();
if (sm != null) {
if (zone == null) {
synchronized(DateTimeZone.class) {
cDefault = zone;
}
}

public static final void setCurrentMillisSystem() throws SecurityException {
checkPermission();
cMillisProvider = SYSTEM_MILLIS_PROVIDER;
}

public int getDays() {
return getPeriodType().getIndexedField(this, PeriodType.DAY_INDEX);
}

public int getMillis() {
return getPeriodType().getIndexedField(this, PeriodType.MILLI_INDEX);
}

public Period(int years, int months, int weeks, int days,
super(years, months, weeks, days, hours, minutes, seconds, millis, PeriodType.standard());
}

public static final void setCurrentMillisFixed(long fixedMillis) throws SecurityException {
checkPermission();
cMillisProvider = new FixedMillisProvider(fixedMillis);
}
