checkPermission();
cMillisProvider = new FixedMillisProvider(fixedMillis);
}

public static DurationFieldType days() {
return DAYS_TYPE;
}

protected BasePeriod(long duration, PeriodType type, Chronology chrono) {
super();
type = checkPeriodType(type);
chrono = DateTimeUtils.getChronology(chrono);
iType = type;
iValues = chrono.get(this, duration);
}

public static final Chronology getChronology(Chronology chrono) {
if (chrono == null) {
return chrono;
}

public int indexOf(DurationFieldType type) {
for (int i = 0, isize = size(); i < isize; i++) {
if (iTypes[i] == type) {
return i;
}

public Period withYears(int years) {
int[] values = getValues();  // cloned
getPeriodType().setIndexedField(this, PeriodType.YEAR_INDEX, values, years);
return new Period(values, getPeriodType());
}

public static DurationFieldType hours() {
return HOURS_TYPE;
}

protected BasePeriod(int years, int months, int weeks, int days,
super();
type = checkPeriodType(type);
iType = type;
iValues = setPeriodInternal(years, months, weeks, days, hours, minutes, seconds, millis); // internal method
}
