public void finish() throws IOException {
if(haveUnclosedEntry) {
throw new IOException("This archives contains unclosed entries.");
}

public CpioArchiveEntry(final String name) {
this(FORMAT_NEW);
this.name = name;
}

public JarArchiveEntry(String name) {
super(name);
}

public int hashCode() {
return super.hashCode();
}

public void putArchiveEntry(ArchiveEntry ze) throws IOException {
if (!jarMarkerAdded) {
((ZipArchiveEntry)ze).addAsFirstExtraField(JarMarker.getInstance());
jarMarkerAdded = true;
super.putArchiveEntry(ze);
}

public static JarMarker getInstance() {
return DEFAULT;
}

public TarArchiveEntry(String name) {
this();
name = normalizeFileName(name);
boolean isDir = name.endsWith("/");
this.devMajor = 0;
this.devMinor = 0;
this.name = name;
this.mode = isDir ? DEFAULT_DIR_MODE : DEFAULT_FILE_MODE;
this.linkFlag = isDir ? LF_DIR : LF_NORMAL;
this.userId = 0;
this.groupId = 0;
this.size = 0;
this.modTime = (new Date()).getTime() / MILLIS_PER_SECOND;
this.linkName = "";
this.userName = "";
this.groupName = "";
this.devMajor = 0;
this.devMinor = 0;
}

public ArArchiveEntry(String name, long length) {
this(name, length, 0, 0, DEFAULT_MODE, System.currentTimeMillis());
}

public ArArchiveEntry(String name, long length, int userId, int groupId, int mode, long lastModified) {
this.name = name;
this.length = length;
this.userId = userId;
this.groupId = groupId;
this.mode = mode;
this.lastModified = lastModified;
}

public String getName() {
return name;
}

public int getUserId() {
return userId;
}

public int getGroupId() {
return groupId;
}

public int getMode() {
return mode;
}

public long getLastModified() {
return lastModified;
}

public long getLength() {
return length;
}

public ArArchiveOutputStream( final OutputStream pOut ) {
this.out = pOut;
}

private long writeArchiveHeader() throws IOException {
byte [] header = ArchiveUtils.toAsciiBytes(ArArchiveEntry.HEADER);
out.write(header);
return header.length;
}

public void putArchiveEntry( final ArchiveEntry pEntry ) throws IOException {
ArArchiveEntry pArEntry = (ArArchiveEntry)pEntry;
if (prevEntry == null) {
archiveOffset += writeArchiveHeader();
prevEntry = pArEntry;
archiveOffset += writeEntryHeader(pArEntry);
entryOffset = 0;
haveUnclosedEntry = true;
}

private long fill( final long pOffset, final long pNewOffset, final char pFill ) throws IOException {
final long diff = pNewOffset - pOffset;
if (diff > 0) {
for (int i = 0; i < diff; i++) {
write(pFill);
return pNewOffset;
}

private long write( final String data ) throws IOException {
final byte[] bytes = data.getBytes("ascii");
write(bytes);
return bytes.length;
}
