aFreq -= mtfFreq[ge--];
final byte[] len_np = len[nPart - 1];
for (int v = alphaSize; --v >= 0;) {
if ((v >= gs) && (v <= ge)) {
len_np[v] = LESSER_ICOST;
len_np[v] = GREATER_ICOST;
gs = ge + 1;
remF -= aFreq;
}

