long signedSum = 0;
int digits = 0;
for (int i = 0; i < header.length; i++) {
byte b = header[i];
if (CHKSUM_OFFSET  <= i && i < CHKSUM_OFFSET + CHKSUMLEN) {
if ('0' <= b && b <= '7' && digits++ < 6) {
storedSum = storedSum * 8 + b - '0';
} else if (digits > 0) {
digits = 6; // only look at the first octal digit sequence
b = ' ';
unsignedSum += 0xff & b;
signedSum += b;
return storedSum == unsignedSum || storedSum == signedSum
}

