public static int readFully(InputStream input, byte[] b, int offset, int len)
if (len < 0 || offset < 0 || len + offset > b.length) {
int count = 0, x = 0;
while (count != len) {
x = input.read(b, offset + count, len - count);
if (x == -1) {
break;
count += x;
return count;
}

