static boolean isUTF8(String charsetName) {
if (charsetName == null) {
charsetName = System.getProperty("file.encoding");
if (Charsets.UTF_8.name().equalsIgnoreCase(charsetName)) {
return true;
for (String alias : Charsets.UTF_8.aliases()) {
