if (off >= 0) {
archive.seek(off);
byte[] sig = ZipArchiveOutputStream.EOCD_SIG;
int curr = archive.read();
while (off >= stopSearching && curr != -1) {
if (curr == sig[POS_0]) {
curr = archive.read();
if (curr == sig[POS_1]) {
curr = archive.read();
