1007: int recordNumber = 0;
1008: Iterator<CSVRecord> iter = parser.iterator();
1009: recordNumber = 0;
1010: while (iter.hasNext()) {
1011: CSVRecord record = iter.next();
1012: recordNumber++;
1013: assertEquals(String.valueOf(recordNumber), record.get(0));
1014: if (recordNumber >= 2) {
1015: break;
1016: }
1017: }
1018: iter.hasNext();
1019: while (iter.hasNext()) {
1020: CSVRecord record = iter.next();
1021: recordNumber++;
1022: assertEquals(String.valueOf(recordNumber), record.get(0));
1023: }
1024: 1025: // Consecutive enhanced for loops shouldn't break sequence
1026: parser = CSVFormat.DEFAULT.parse(new StringReader(fiveRows));
1027: recordNumber = 0;
1028: for (CSVRecord record : parser) {
1029: recordNumber++;
1030: assertEquals(String.valueOf(recordNumber), record.get(0));
